import React, { useState } from 'react';
import './QuotationComponent.css';
import { NavLink } from 'react-router-dom';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';

const QuotationComponent = () => {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({
    name: '',
    description: '',
    unitPrice: '',
    quantity: '',
    image: '',
  });
  const [clientDetails, setClientDetails] = useState({
    clientName: '',
    clientNumber: '',
    date: '',
    address: '',
  });
  const [info, setInfo] = useState({
    clientName: '',
    clientNumber: '',
    date: '',
    address: '',
    products: [] // ← array inside the object
  });
  const [editingProductId, setEditingProductId] = useState(null);
  const [cgstRate, setCgstRate] = useState(14);
  const [sgstRate, setSgstRate] = useState(14);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAddProduct = (e) => {
    e.preventDefault();
    const { name, description, unitPrice, quantity, image } = form;

    if (!name || !unitPrice || !quantity) return;

    const total = parseFloat(unitPrice) * parseInt(quantity);

    if (editingProductId !== null) {
      const updatedProducts = products.map((p) =>
        p.id === editingProductId
          ? {
            ...p,
            name,
            description,
            unitPrice: parseFloat(unitPrice),
            quantity: parseInt(quantity),
            total,
            image,
          }
          : p
      );
      setProducts(updatedProducts);
      setEditingProductId(null);
    } else {
      const newProduct = {
        id: Date.now(),
        name,
        description,
        unitPrice: parseFloat(unitPrice),
        quantity: parseInt(quantity),
        total,
        image,
      };
      setProducts([...products, newProduct]);
    }

    setForm({ name: '', description: '', unitPrice: '', quantity: '', image: '' });
  };

  const handleRemoveProduct = (id) => {
    setProducts(products.filter(p => p.id !== id));
    if (editingProductId === id) {
      setEditingProductId(null);
      setForm({ name: '', description: '', unitPrice: '', quantity: '', image: '' });
    }
  };

  const handleEditProduct = (product) => {
    setForm({
      name: product.name,
      description: product.description,
      unitPrice: product.unitPrice,
      quantity: product.quantity,
      image: product.image,
    });
    setEditingProductId(product.id);
  };

  const grandTotal = products.reduce((sum, p) => sum + p.total, 0);
  const cgst = (grandTotal * cgstRate) / 100;
  const sgst = (grandTotal * sgstRate) / 100;
  const finalTotal = grandTotal + cgst + sgst;

  function formatCurrency(value) {
    return "Rs. " + Number(value).toLocaleString("en-IN", {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    });
  }
  function numberToWordsIndian(num) {
    const a = [
      "", "One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine",
      "Ten", "Eleven", "Twelve", "Thirteen", "Fourteen", "Fifteen",
      "Sixteen", "Seventeen", "Eighteen", "Nineteen"
    ];
    const b = ["", "", "Twenty", "Thirty", "Forty", "Fifty", "Sixty", "Seventy", "Eighty", "Ninety"];

    function inWords(n) {
      if (n < 20) return a[n];
      if (n < 100) return b[Math.floor(n / 10)] + (n % 10 ? " " + a[n % 10] : "");
      if (n < 1000) return a[Math.floor(n / 100)] + " Hundred" + (n % 100 ? " " + inWords(n % 100) : "");
      if (n < 100000) return inWords(Math.floor(n / 1000)) + " Thousand" + (n % 1000 ? " " + inWords(n % 1000) : "");
      if (n < 10000000) return inWords(Math.floor(n / 100000)) + " Lakh" + (n % 100000 ? " " + inWords(n % 100000) : "");
      return inWords(Math.floor(n / 10000000)) + " Crore" + (n % 10000000 ? " " + inWords(n % 10000000) : "");
    }

    const parts = num.toFixed(2).split(".");
    const rupees = parseInt(parts[0]);
    const paise = parseInt(parts[1]);

    let words = inWords(rupees) + " Rupees";
    if (paise > 0) {
      words += " and " + inWords(paise) + " Paise";
    }
    return words;
  }



  // Client Info
  const handleClientDetailsChange = (e) => {
    const { name, value } = e.target;
    setClientDetails(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleClientDetailsSubmit = (e) => {
    e.preventDefault();
    // You can save this to localStorage or do further processing

  };

  // Client Info

  // download pdf
  // download pdf
  // download pdf
  const downloadPdf = () => {


    const doc = new jsPDF();
    const pageWidth = doc.internal.pageSize.getWidth();
    let y = 20;
    const headerHeight = 25;

    // 0. Draw orange vertical strip on the left
    doc.setFillColor(238, 128, 26); // #ee801a
    doc.rect(0, 0, 8, doc.internal.pageSize.getHeight(), 'F'); // (x, y, width, height)

    // 1. Insert logo (left top corner)
    const logoBase64 = 'data:image/jpeg;base64,'; // replace with your actual base64
    doc.addImage(logoBase64, 'PNG', 12, 10, 34, 24);
    

    // 2. Add company name & subtitle beside logo
    doc.setFontSize(16);
    doc.setTextColor(33, 37, 41); // dark gray
    doc.text("CHROMATICS", 48, 20); // ("", x, y)

    doc.setFontSize(10);
    doc.setTextColor(120);
    doc.text("HOME AUTOMATION", 48, 27);
    doc.text("AV SOLUTIONS", 48, 32);

    // 3. Draw orange block on right side
    doc.setFillColor(238, 128, 26); // #ee801a
    doc.rect(pageWidth - 90, 10, 80, headerHeight, 'F'); // (x, y, width, height, style='F' for filled)

    // 4. Add contact details in orange block
    doc.setFontSize(9);
    doc.setTextColor(255); // white text

    doc.text("+91 9209628554", pageWidth - 85, 16);
    doc.text("help@chromatics2024.com", pageWidth - 85, 20);
    doc.text("Plot No. 31, Near Hingna T-point,", pageWidth - 85, 24);
    doc.text("Below Rachna Ring Metro Station,", pageWidth - 85, 28);
    doc.text("Nagpur - 440036", pageWidth - 85, 32);

    y += 30;
    doc.setFontSize(11);
    doc.setTextColor(33, 37, 41);
    y += 10;
    const leftMargin = 16;

    doc.setFontSize(14);
    doc.text("Quotation", pageWidth / 2, y, { align: 'center' });
    y += 10;

    doc.text(`${clientDetails.clientName}`, leftMargin, y);
    y += 7;

    const splitAddress = doc.splitTextToSize(clientDetails.address, 50);
    doc.text(splitAddress, leftMargin, y);
    y += splitAddress.length * 6;


    doc.text(`${clientDetails.clientNumber}`, leftMargin, y);
    y += 7;
    doc.text(`${clientDetails.date}`, leftMargin, y);
    y += 7;



    const headers = [["Sr.", "Product", "Description", "Unit Price", "Qty", "Total"]];
    const body = products.map((p, index) => [
      index + 1,
      p.name,
      p.description,
      formatCurrency(p.unitPrice),
      p.quantity,
      formatCurrency(p.total)
    ]);

    autoTable(doc, {
      startY: y,
      head: headers,
      body: body,
      styles: { fontSize: 9 },
      headStyles: { fillColor: [238, 128, 26], textColor: 255 },
    });

    y = doc.lastAutoTable.finalY + 10;
    const boxY = y + 10;
    const boxHeight = 50;
    const boxPadding = 4;

    // 1. Left box - Bank and Tax Info
    const leftX = 14;
    const middleX = pageWidth / 2 - 20;
    const rightX = pageWidth - 70;

    doc.setDrawColor(0);
    doc.setLineWidth(0.2);

    // Draw outer boxes
    doc.rect(leftX, boxY, pageWidth - 28, boxHeight); // whole outer box

    // Left Column
    doc.setFontSize(9);
    doc.setTextColor(0);
    const amountInWords = numberToWordsIndian(finalTotal);

    doc.text(amountInWords, leftX + boxPadding, boxY + 5, {
      maxWidth: 50 // Adjust this width as needed
    });
    doc.text("GST No: 27AAVCA2******", leftX + boxPadding, boxY + 20);
    doc.text("PAN No: AAVCA*****", leftX + boxPadding, boxY + 25);
    doc.text("TAN No: MUMA6*****", leftX + boxPadding, boxY + 30);
    doc.text("Beneficiary name: CHROMATICS PVT LTD", leftX + boxPadding, boxY + 35);
    doc.text("Kotak Mahindra Bank (Santacruz West)", leftX + boxPadding, boxY + 39);
    doc.text("Account No : 5545#####", leftX + boxPadding, boxY + 43);
    doc.text("IFSC- KKBK0*****", leftX + boxPadding, boxY + 48);

    // 2. Center - QR Code
    // Make sure `qrBase64` is a valid Base64 image or data URL
    const qrBase64 = 'data:image/png;base64,'; // <-- replace this with actual base64 QR image
    doc.addImage(qrBase64, "PNG", middleX, boxY + 8, 30, 30);
    doc.setFontSize(8);
    doc.text("PAYMENT QR CODE", middleX + 4, boxY + 6);

    // 3. Right - Totals
    doc.setFontSize(9);
    doc.setTextColor(0);

    const rightTextX = rightX + 2;
    let rightTextY = boxY + 10;

    // Assume total width of the box is 60
    const boxWidth = 40;
    const valueX = rightTextX + boxWidth;

    doc.setFontSize(9);
    doc.setTextColor(0);

    doc.text("Sub Total", rightTextX, rightTextY);
    doc.text(formatCurrency(grandTotal).toString(), valueX, rightTextY, { align: "right" });

    rightTextY += 8;
    doc.text(`SGST ${sgstRate}%`, rightTextX, rightTextY);
    doc.text(formatCurrency(sgst).toString(), valueX, rightTextY, { align: "right" });

    rightTextY += 8;
    doc.text(`CGST ${cgstRate}%`, rightTextX, rightTextY);
    doc.text(formatCurrency(cgst).toString(), valueX, rightTextY, { align: "right" });

    rightTextY += 8;
    doc.setFontSize(10);
    doc.setFont(undefined, "bold");
    doc.text("Amount", rightTextX, rightTextY);
    doc.text(formatCurrency(finalTotal).toString(), valueX, rightTextY, { align: "right" });
    doc.setFont(undefined, "normal");




    // footer
    const pageHeight = doc.internal.pageSize.getHeight();

    const footerY = pageHeight - 20; // 20mm from bottom (adjust as needed)

    // Draw left text
    doc.setFontSize(10);
    doc.setTextColor(50);
    doc.setFont("helvetica", "normal");
    doc.text("Home Automation | AV Solutions", 14, footerY);

    // Draw right orange rectangle
    const rectWidth = 80; // Adjust width
    const rectHeight = 10;
    const rectX = pageWidth - rectWidth - 14; // 14mm padding from right
    const rectY = footerY - rectHeight + 2; // Align with text vertically

    doc.setFillColor(235, 112, 23); // Orange color (adjust if needed)
    doc.rect(rectX, rectY, rectWidth, rectHeight, "F");

    doc.save(`quotation_${Date.now()}.pdf`);
    // to download pdf

    const updatedInfo = {
      ...info,
      clientName: clientDetails.clientName,
      clientNumber: clientDetails.clientNumber,
      date: clientDetails.date,
      address: clientDetails.address,
      products: [...products],
    };
    
    console.log("Updated Info:", updatedInfo); // ✅ Logs correct state
    setInfo(updatedInfo);
    

    // console.log("All Info : ", info);
    // console.log("Client Details Saved:", clientDetails);
    // console.log(products);
    alert("Client details saved!");


    setClientDetails({
      clientName: '',
      clientNumber: '',
      date: '',
      address: '',
    })
  };
  // pdf
  return (
    <div className="quotation-container">
      <div className="left-align">
        <NavLink to="/quotation" className="nav-link">
          <button className="submit">Show All Quotation</button>
        </NavLink>
      </div>

      <button onClick={downloadPdf} className="DownloadPdfButton">Download PDF</button>
      <br />
      <form onSubmit={handleClientDetailsSubmit} className="client-form">
        <input
          type="text"
          name="clientName"
          placeholder="Client Name"
          value={clientDetails.clientName}
          onChange={handleClientDetailsChange}
          required
        />
        <input
          type="tel"
          name="clientNumber"
          placeholder="Client Number"
          maxLength={10}
          value={clientDetails.clientNumber}
          onChange={handleClientDetailsChange}
          required
        />
        <input
          name="address"
          type="textarea"
          placeholder="Client Address"
          value={clientDetails.address}
          onChange={handleClientDetailsChange}
          required
        />
        <input
          type="date"
          name="date"
          value={clientDetails.date}
          onChange={handleClientDetailsChange}
          required
        />
        
        {/* <button type="submit">Save Client Details</button> */}
      </form>
      <br />
      <form onSubmit={handleAddProduct} className="product-form">
        <input type="text" name="image" placeholder="Image URL" value={form.image} onChange={handleChange} />
        <input type="text" name="name" placeholder="Product Name" value={form.name} onChange={handleChange} required />
        <input type="text" name="description" placeholder="Description" value={form.description} onChange={handleChange} />
        <input type="number" name="unitPrice" placeholder="Unit Price" min="0" value={form.unitPrice} onChange={handleChange} required />
        <input type="number" name="quantity" placeholder="Qty" min="0" value={form.quantity} onChange={handleChange} required />

        <button type="submit">{editingProductId ? 'Update Product' : 'Add Product'}</button>
        {editingProductId && (
          <button type="button" onClick={() => {
            setEditingProductId(null);
            setForm({ name: '', description: '', unitPrice: '', quantity: '', image: '' });
          }}>Cancel</button>
        )}

      </form>

      {products.length > 0 && (
        <div className="table-container">
          <div className="tax-selectors">
            <label>
              CGST Rate:
              <select value={cgstRate} onChange={(e) => setCgstRate(Number(e.target.value))}>
                <option value={0}>0%</option>
                <option value={4}>4%</option>
                <option value={8}>8%</option>
                <option value={12}>12%</option>
                <option value={14}>14%</option>
              </select>
            </label>
            &nbsp;&nbsp;
            <label>
              SGST Rate:
              <select value={sgstRate} onChange={(e) => setSgstRate(Number(e.target.value))}>
                <option value={0}>0%</option>
                <option value={4}>4%</option>
                <option value={8}>8%</option>
                <option value={12}>12%</option>
                <option value={14}>14%</option>
              </select>
            </label>
          </div>

          <table>
            <thead>
              <tr>
                <th>Image</th>
                <th>Product</th>
                <th>Description</th>
                <th>Unit Price</th>
                <th>Qty</th>
                <th>Total</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {products.map(product => (
                <tr key={product.id}>
                  <td>
                    {product.image ? (
                      <img src={product.image} alt={product.name} className="product-image" />
                    ) : (
                      <div className="no-image">No Image</div>
                    )}
                  </td>
                  <td>{product.name}</td>
                  <td>{product.description}</td>
                  <td>₹{product.unitPrice.toFixed(2)}</td>
                  <td>{product.quantity}</td>
                  <td>₹{product.total.toFixed(2)}</td>
                  <td>
                    <button onClick={() => handleEditProduct(product)} className="edit">
                      <svg xmlns="http://www.w3.org/2000/svg" height="24px" viewBox="0 -960 960 960" width="24px"
                        fill="#1f1f1f"><path d="M200-200h57l391-391-57-57-391 391v57Zm-80 80v-170l528-527q12-11 26.5-17t30.5-6q16 0 31 6t26 18l55 56q12 11 17.5 26t5.5 30q0 16-5.5 30.5T817-647L290-120H120Zm640-584-56-56 56 56Zm-141 85-28-29 57 57-29-28Z" />
                      </svg>
                    </button>
                    <button onClick={() => handleRemoveProduct(product.id)} className="remove">
                      <svg xmlns="http://www.w3.org/2000/svg" height="24px" viewBox="0 -960 960 960" width="24px"
                        fill="#1f1f1f"><path d="M280-120q-33 0-56.5-23.5T200-200v-520h-40v-80h200v-40h240v40h200v80h-40v520q0 33-23.5 56.5T680-120H280Zm400-600H280v520h400v-520ZM360-280h80v-360h-80v360Zm160 0h80v-360h-80v360ZM280-720v520-520Z" />
                      </svg>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
            <tfoot>
              <tr>
                <td colSpan="5" className="right-align">Sub Total:</td>
                <td>₹{grandTotal.toFixed(2)}</td>
                <td></td>
              </tr>
              <tr>
                <td colSpan="5" className="right-align">CGST ({cgstRate}%):</td>
                <td>₹{cgst.toFixed(2)}</td>
                <td></td>
              </tr>
              <tr>
                <td colSpan="5" className="right-align">SGST ({sgstRate}%):</td>
                <td>₹{sgst.toFixed(2)}</td>
                <td></td>
              </tr>
              <tr className="grand-total">
                <td colSpan="5" className="right-align"><strong>Grand Total:</strong></td>
                <td><strong>₹{finalTotal.toFixed(2)}</strong></td>
                <td></td>
              </tr>
            </tfoot>
          </table>
        </div>
      )}
    </div>
  );
};

export default QuotationComponent;
